  bool access::object_traits_impl< ::UniPAX::PublicationXref, id_mysql >::
  grow (image_type& i,
        my_bool* t,
        std::size_t d)
  {
    ODB_POTENTIALLY_UNUSED (i);
    ODB_POTENTIALLY_UNUSED (t);

    bool grew (false);

    // Xref base
    //
    if (--d != 0)
    {
      if (base_traits::grow (*i.base, t + 2UL))
        i.base->version++;
    }

    // title
    //
    if (t[0UL])
    {
      i.title_value.capacity (i.title_size);
      grew = true;
    }

    // year
    //
    t[1UL] = 0;

    return grew;
  }

  void access::object_traits_impl< ::UniPAX::PublicationXref, id_mysql >::
  bind (MYSQL_BIND* b,
        const MYSQL_BIND* id,
        std::size_t id_size,
        image_type& i,
        mysql::statement_kind sk)
  {
    ODB_POTENTIALLY_UNUSED (sk);

    using namespace mysql;

    std::size_t n (0);

    // unipaxId
    //
    if (sk == statement_insert)
    {
      if (id != 0)
        std::memcpy (&b[n], id, id_size * sizeof (id[0]));
      n += id_size;
    }

    // title
    //
    b[n].buffer_type = MYSQL_TYPE_STRING;
    b[n].buffer = i.title_value.data ();
    b[n].buffer_length = static_cast<unsigned long> (
      i.title_value.capacity ());
    b[n].length = &i.title_size;
    b[n].is_null = &i.title_null;
    n++;

    // year
    //
    b[n].buffer_type = MYSQL_TYPE_LONG;
    b[n].is_unsigned = 0;
    b[n].buffer = &i.year_value;
    b[n].is_null = &i.year_null;
    n++;

    // unipaxId
    //
    if (sk == statement_update)
    {
      if (id != 0)
        std::memcpy (&b[n], id, id_size * sizeof (id[0]));
      n += id_size;
    }

    // Xref base
    //
    if (sk == statement_select)
      base_traits::bind (b + n, *i.base, sk);
  }

  bool access::object_traits_impl< ::UniPAX::PublicationXref, id_mysql >::
  init (image_type& i,
        const object_type& o,
        mysql::statement_kind sk)
  {
    ODB_POTENTIALLY_UNUSED (i);
    ODB_POTENTIALLY_UNUSED (o);
    ODB_POTENTIALLY_UNUSED (sk);

    using namespace mysql;

    bool grew (false);

    // title
    //
    {
      ::std::string const& v =
        o.title;

      bool is_null (false);
      std::size_t size (0);
      std::size_t cap (i.title_value.capacity ());
      mysql::value_traits<
          ::std::string,
          mysql::id_string >::set_image (
        i.title_value,
        size,
        is_null,
        v);
      i.title_null = is_null;
      i.title_size = static_cast<unsigned long> (size);
      grew = grew || (cap != i.title_value.capacity ());
    }

    // year
    //
    {
      int const& v =
        o.year;

      bool is_null (false);
      mysql::value_traits<
          int,
          mysql::id_long >::set_image (
        i.year_value, is_null, v);
      i.year_null = is_null;
    }

    return grew;
  }

