  bool access::object_traits_impl< ::UniPAX::Series, id_mysql >::
  grow (image_type& i,
        my_bool* t)
  {
    ODB_POTENTIALLY_UNUSED (i);
    ODB_POTENTIALLY_UNUSED (t);

    bool grew (false);

    // UIBase base
    //
    if (object_traits_impl< ::UniPAX::UIBase, id_mysql >::grow (
          i, t + 0UL))
      grew = true;

    // name
    //
    if (t[2UL])
    {
      i.name_value.capacity (i.name_size);
      grew = true;
    }

    // description
    //
    if (t[3UL])
    {
      i.description_value.capacity (i.description_size);
      grew = true;
    }

    return grew;
  }

  void access::object_traits_impl< ::UniPAX::Series, id_mysql >::
  bind (MYSQL_BIND* b,
        image_type& i,
        mysql::statement_kind sk)
  {
    ODB_POTENTIALLY_UNUSED (sk);

    using namespace mysql;

    std::size_t n (0);

    // UIBase base
    //
    object_traits_impl< ::UniPAX::UIBase, id_mysql >::bind (b + n, i, sk);
    n += sk == statement_update ? 1UL : 2UL;

    // name
    //
    b[n].buffer_type = MYSQL_TYPE_STRING;
    b[n].buffer = i.name_value.data ();
    b[n].buffer_length = static_cast<unsigned long> (
      i.name_value.capacity ());
    b[n].length = &i.name_size;
    b[n].is_null = &i.name_null;
    n++;

    // description
    //
    b[n].buffer_type = MYSQL_TYPE_STRING;
    b[n].buffer = i.description_value.data ();
    b[n].buffer_length = static_cast<unsigned long> (
      i.description_value.capacity ());
    b[n].length = &i.description_size;
    b[n].is_null = &i.description_null;
    n++;
  }

  bool access::object_traits_impl< ::UniPAX::Series, id_mysql >::
  init (image_type& i,
        const object_type& o,
        mysql::statement_kind sk)
  {
    ODB_POTENTIALLY_UNUSED (i);
    ODB_POTENTIALLY_UNUSED (o);
    ODB_POTENTIALLY_UNUSED (sk);

    using namespace mysql;

    bool grew (false);

    // UIBase base
    //
    if (object_traits_impl< ::UniPAX::UIBase, id_mysql >::init (i, o, sk))
      grew = true;

    // name
    //
    {
      ::std::string const& v =
        o.name;

      bool is_null (false);
      std::size_t size (0);
      std::size_t cap (i.name_value.capacity ());
      mysql::value_traits<
          ::std::string,
          mysql::id_string >::set_image (
        i.name_value,
        size,
        is_null,
        v);
      i.name_null = is_null;
      i.name_size = static_cast<unsigned long> (size);
      grew = grew || (cap != i.name_value.capacity ());
    }

    // description
    //
    {
      ::std::string const& v =
        o.description;

      bool is_null (false);
      std::size_t size (0);
      std::size_t cap (i.description_value.capacity ());
      mysql::value_traits<
          ::std::string,
          mysql::id_string >::set_image (
        i.description_value,
        size,
        is_null,
        v);
      i.description_null = is_null;
      i.description_size = static_cast<unsigned long> (size);
      grew = grew || (cap != i.description_value.capacity ());
    }

    return grew;
  }

