  void access::object_traits< ::UniPAX::PublicationXref >::
  bind (oracle::bind* b, image_type& i, oracle::statement_kind sk)
  {
    ODB_POTENTIALLY_UNUSED (sk);

    using namespace oracle;

    std::size_t n (0);

    // Xref base
    //
    object_traits< ::UniPAX::Xref >::bind (b + n, i, sk);
    n += 6UL - (
      sk == statement_select ? 0 : (
        sk == statement_insert ? 0 : 1UL));

    // title
    //
    b[n].type = oracle::bind::string;
    b[n].buffer = i.title_value;
    b[n].capacity = static_cast<ub4> (sizeof (i.title_value));
    b[n].size = &i.title_size;
    b[n].indicator = &i.title_indicator;
    n++;

    // year
    //
    b[n].type = oracle::bind::integer;
    b[n].buffer = &i.year_value;
    b[n].capacity = 4;
    b[n].size = 0;
    b[n].indicator = &i.year_indicator;
    n++;
  }

  bool access::object_traits< ::UniPAX::PublicationXref >::
  init (image_type& i, const object_type& o, oracle::statement_kind sk)
  {
    ODB_POTENTIALLY_UNUSED (i);
    ODB_POTENTIALLY_UNUSED (o);
    ODB_POTENTIALLY_UNUSED (sk);

    using namespace oracle;

    if (i.change_callback_.callback != 0)
      (i.change_callback_.callback) (i.change_callback_.context);

    bool grew (false);

    // Xref base
    //
    if (object_traits< ::UniPAX::Xref >::init (i, o, sk))
      grew = true;

    // title
    //
    {
      bool is_null;
      std::size_t size (0);
      oracle::value_traits<
          ::std::string,
          oracle::id_string >::set_image (
        i.title_value,
        sizeof (i.title_value),
        size,
        is_null,
        o.title);
      i.title_size = static_cast<ub2> (size);
      i.title_indicator = is_null ? -1 : 0;
    }

    // year
    //
    {
      bool is_null;
      oracle::value_traits<
          int,
          oracle::id_int32 >::set_image (
        i.year_value, is_null, o.year);
      i.year_indicator = is_null ? -1 : 0;
    }

    return grew;
  }

  void access::object_traits< ::UniPAX::PublicationXref >::
  init (object_type& o, const image_type& i, database& db)
  {
    ODB_POTENTIALLY_UNUSED (o);
    ODB_POTENTIALLY_UNUSED (i);
    ODB_POTENTIALLY_UNUSED (db);

    // Xref base
    //
    object_traits< ::UniPAX::Xref >::init (o, i, db);

    // title
    //
    oracle::value_traits<
        ::std::string,
        oracle::id_string >::set_value (
      o.title,
      i.title_value,
      i.title_size,
      i.title_indicator == -1);

    // year
    //
    oracle::value_traits<
        int,
        oracle::id_int32 >::set_value (
      o.year,
      i.year_value,
      i.year_indicator == -1);
  }

